mapper.select:
SELECT DEL_YN FROM like_board
WHERE
    BOARD_IDX = #{boardIdx}
AND BOARD_TYPE = #{boardType}
AND USER_ID = #{userId}

mapper.insert:
INSERT INTO like_board
(
    IDX
    ,BOARD_IDX
    ,BOARD_TYPE
    ,USER_ID
    ,DEL_YN
)
VALUES
(
    (SELECT NVL(MAX(idx), 0) + 1 FROM like_board ALIAS_FOR_SUBQUERY)
    , #{boardIdx}
    , #{boardType}
    , #{userId}
    , 'N'
)

mapper.update:
UPDATE
    like_board
SET
    DEL_YN = 'N'
WHERE
    BOARD_IDX = #{boardIdx}
AND BOARD_TYPE = #{boardType}
AND USER_ID = #{userId}

UPDATE
    like_board
SET
    DEL_YN = 'Y'
WHERE
    BOARD_IDX = #{boardIdx}
AND BOARD_TYPE = #{boardType}
AND USER_ID = #{userId}
