mapper.select:
SELECT DEL_YN
FROM
    like_board
WHERE USER_ID = #{userId}
AND BOARD_TYPE = #{boardType}
AND BOARD_IDX = #{boardIdx}

mapper.insert:
INSERT INTO like_board
(
    IDX
    ,USER_ID
    ,BOARD_IDX
    ,BOARD_TYPE
    ,DEL_YN
)
VALUES
(
    (SELECT NVL(MAX(idx), 0) + 1 FROM like_board ALIAS_FOR_SUBQUERY)
    , #{userId}
    , #{boardIdx}
    , #{boardType}
    , 'N'
)

mapper.update:
UPDATE
    like_board
SET
    DEL_YN = 'N'
WHERE USER_ID = #{userId}
AND BOARD_TYPE = #{boardType}
AND BOARD_IDX = #{boardIdx}

UPDATE
    like_board
SET
    DEL_YN = 'Y'
WHERE USER_ID = #{userId}
AND BOARD_TYPE = #{boardType}
AND BOARD_IDX = #{boardIdx}
