mapper.select:
SELECT A.WRITER
      ,B.FILE_NAME
  FROM
       (SELECT DISTINCT WRITER
          FROM COMMENT
         WHERE BOARD_TYPE = #{boardType}
           AND BOARD_IDX = #{boardIdx}
           AND DEL_YN = 'N'
       ) A
  LEFT OUTER JOIN
       (SELECT FILE_NAME
          FROM FILE
         WHERE DLT_YN = 'N'
       ) B
    ON CONCAT('mem||', A.WRITER) = B.BRD_REF;

mapper.insert:
INSERT INTO COMMENT
       (NUM
       ,BOARD_IDX
       ,BOARD_TYPE
       ,CONTENT
       ,WRITER
       ,REG_DATE
       ,DEL_YN
      )
VALUES
      ((SELECT NVL(MAX(num), 0) + 1 FROM comment ALIAS_FOR_SUBQUERY)
       ,#{boardIdx}
       ,#{boardType}
       ,#{content}
       ,#{writer}
       ,(SELECT date_format(now(),'%Y-%c-%d'))
       ,'N'
      )

mapper.update:
UPDATE COMMENT
   SET DEL_YN = 'Y'
 WHERE BOARD_TYPE = #{boardType}
   AND BOARD_IDX = #{boardIdx}

UPDATE COMMENT
   SET DEL_YN = 'Y'
 WHERE BOARD_TYPE = #{boardType}
   AND BOARD_IDX = #{boardIdx}
   AND NUM = #{num}

UPDATE COMMENT
   SET CONTENT = #{content}
      ,UPD_DATE = (SELECT date_format(now(),'%Y-%c-%d'))
 WHERE NUM = #{num}

mapper.select:
SELECT COUNT(*) AS Count
  FROM COMMENT
 WHERE BOARD_TYPE = #{boardType}
   AND BOARD_IDX = #{boardIdx}
   AND DEL_YN = 'N'
