mapper.insert:
INSERT INTO comment
(
    NUM
    ,BOARD_IDX
    ,BOARD_TYPE
    , CONTENT
    , WRITER
    , REG_DATE
    , DEL_YN
)
VALUES
(
    (SELECT NVL(MAX(num), 0) + 1 FROM comment ALIAS_FOR_SUBQUERY)
    , #{boardIdx}
    , #{boardType}
    , #{content}
    , #{writer}
    , (SELECT date_format(now(),'%Y-%c-%d'))
    , 'N'
)

mapper.update:
UPDATE
    comment
SET
    DEL_YN = 'Y'
WHERE BOARD_TYPE = #{boardType}
AND BOARD_IDX = #{boardIdx}

UPDATE
    comment
SET
    DEL_YN = 'Y'
WHERE BOARD_TYPE = #{boardType}
AND BOARD_IDX = #{boardIdx}
AND NUM = #{num}

UPDATE
    comment
SET
    CONTENT = #{content}
    , UPD_DATE = (SELECT date_format(now(),'%Y-%c-%d'))
WHERE NUM = #{num}
