, #{category}
, #{matzipData}
, #{addressName}
)

INSERT INTO RECM_BOARD
( REF_MATZIP
, WRITER
, SUBJECT
, STAR
, CONTENT
)
VALUES
( #{refMatzip}
, #{writer}
, #{subject}
, #{star}
, #{content}
)

mapper.update:
UPDATE MATZIP_BOARD
SET CLOSED_STATE = 'Y'
WHERE (
SELECT
COUNT(*)
FROM RECM_BOARD
WHERE REF_MATZIP = #{id}
AND DEL_YN = 'N'
) = 0 AND ID = #{id};

UPDATE RECM_BOARD
SET
SUBJECT = #{subject}
, STAR = #{star}
, CONTENT = #{content}
WHERE NUM = #{num}
