mapper.insert:
INSERT INTO MATZIP_BOARD
(   ID
    , CATEGORY
    , MATZIP_DATA
    , ADDRESS_NAME
)
VALUES
(   #{id}
    , #{category}
    , #{matzipData}
    , #{addressName}
)

INSERT INTO RECM_BOARD
( REF_MATZIP
, WRITER
, SUBJECT
, STAR
, CONTENT
)
VALUES
( #{refMatzip}
, #{writer}
, #{subject}
, #{star}
, #{content}
)

mapper.update:
UPDATE RECM_BOARD
SET
SUBJECT = #{subject}
, STAR = #{star}
, CONTENT = #{content}
WHERE NUM = #{num}

mapper.select:
SELECT
COUNT(*) AS MATZIPCOUNT,
REF_MATZIP AS ID
FROM RECM_BOARD
where REF_MATZIP= #{id}
group by REF_MATZIP;

SELECT
ID FROM MATZIP_BOARD
WHERE ADDRESS_NAME = #{loc}
