mapper.insert:
INSERT INTO tip_board
(
    NUM
    ,CATEGORY
    ,WRITER
    ,SUBJECT
    ,CONTENT
    ,REG_DATE
    ,DEL_YN
)
VALUES
(
    (SELECT NVL(MAX(num), 0) + 1 FROM tip_board ALIAS_FOR_SUBQUERY)
    , #{category}
    , #{writer}
    , #{subject}
    , #{content}
    , (SELECT date_format(now(),'%Y-%c-%d'))
    , 'N'
)

mapper.update:
UPDATE
   tip_board
SET
    CATEGORY = #{category}
    , SUBJECT = #{subject}
    , CONTENT = #{content}
    , UPD_DATE = (SELECT date_format(now(),'%Y-%c-%d'))
WHERE NUM = #{num}
